export const useLoading = defineStore('loading', {
  state: () => ({ queue: [] }),
  actions: {
    add(el) {
      this.queue = [...this.queue, el]
    },
    stop(el) {
        this.queue = this.queue.filter(f => f != el)
    }
  },
})

export const useLoading = defineStore('loading', () => {
  const queue = ref([])
  function add(el) {
    queue.value = [...queue.value, el]
  }
  function stop(el) {
    queue.value = queue.value.filter(f => f != el)
  }
  return { queue, add }
})

